// MATCHING EXCERCISE MASTER LOGIC

function generateSentenceMatchingExercise(argument){
        // Generate the matching exercise content dynamically

  
	let selectedFirstHalf = null;
    let selectedSecondHalf = null;
	feedbackMatchingExcercise = argument

    // Randomize sentence halves
    const shuffledFirstHalves = argument.sentenceSets.map(set => set.first).sort(() => 0.5 - Math.random());
    const shuffledSecondHalves = argument.sentenceSets.map(set => set.second).sort(() => 0.5 - Math.random());
    // Create the HTML structure
    let content = "<div class='sentence-matching-game'>";
    content += "<h3>Match the sentence parts:</h3>";

    // Display first halves
    content += "<div class='sentence-halves'><div class='column'>";
    shuffledFirstHalves.forEach((half, index) => {
        content += `<button class="first-half" id="first-${index}" data-index="${index}">${half}</button><br>`;
    });
    content += "</div>";
    // Display second halves
    content += "<div class='column'>";
    shuffledSecondHalves.forEach((half, index) => {
        content += `<button class="second-half" id="second-${index}" data-index="${index}">${half}</button><br>`;
    });
    content += "</div></div>";

    // Add feedback and control buttons
    content += `

        <div id="feedback"></div>
        <button id="validateAnswers">Check Answers</button>
        <button id="regenerateExercise">Regenerate Exercise</button>
    `;
    content += "</div>";
	
    // Regenerate the exercise
    document.addEventListener("click", (event) => {
        if (event.target.id === "regenerateExercise") {
			
            document.querySelector(".sentence-matching-game").innerHTML = generateSentenceMatchingExercise(feedbackMatchingExcercise);
        console.log(feedbackMatchingExcercise)
		}
    });

    // Validate all matches (optional)
    document.addEventListener("click", (event) => {
        if (event.target.id === "validateAnswers") {
            const feedback = document.getElementById("feedback");
            feedback.innerHTML = "The correct matches are:<br>";
            feedbackMatchingExcercise.sentenceSets.forEach(match => {
                feedback.innerHTML += `<span>${match.first} ${match.second}</span><br>`;
            });
        }
    });

    return content;
}

    // Handle button clicks

document.addEventListener("click", (event) => {
    if (event.target.classList.contains("first-half")) {
        selectedFirstHalf = event.target.textContent.trim();
        document.querySelectorAll(".first-half").forEach(btn => btn.classList.remove("selected"));
        event.target.classList.add("selected");
    } else if (event.target.classList.contains("second-half")) {
        selectedSecondHalf = event.target.textContent.trim();
        document.querySelectorAll(".second-half").forEach(btn => btn.classList.remove("selected"));
        event.target.classList.add("selected");
    

    // Validate when both halves are selected
    if (selectedFirstHalf && selectedSecondHalf) {
        const feedback = document.getElementById("feedback");
        const firstButton = Array.from(document.querySelectorAll(".first-half"))
            .find(btn => btn.textContent.trim() === selectedFirstHalf);
        const secondButton = Array.from(document.querySelectorAll(".second-half"))
            .find(btn => btn.textContent.trim() === selectedSecondHalf);

        const match = feedbackMatchingExcercise.sentenceSets.find(
            pair => pair.first === selectedFirstHalf && pair.second === selectedSecondHalf
        );

        if (match) {
            feedback.innerHTML = `<span style="color: green;">Correct! "${selectedFirstHalf} ${selectedSecondHalf}"</span>`;
            // Disable and gray out matched buttons
            firstButton.disabled = true;
            secondButton.disabled = true;
            firstButton.style.backgroundColor = "#d3d3d3"; // Light gray
            secondButton.style.backgroundColor = "#d3d3d3"; // Light gray
            firstButton.style.cursor = "not-allowed";
            secondButton.style.cursor = "not-allowed";
        } else {
            feedback.innerHTML = `<span style="color: red;">Incorrect match: "${selectedFirstHalf}" does not match "${selectedSecondHalf}".</span>`;
        }

        // Reset selections
        document.querySelectorAll(".selected").forEach(btn => btn.classList.remove("selected"));
        selectedFirstHalf = null;
        selectedSecondHalf = null;
    }
}});
